    //     // alert('A name was submitted: ' + this.state.username+' , '+ this.state.password);
    //     // event.preventDefault();
    //   }


    render() {
        return (

            <div>
                <h3> Profile Page</h3>
                {/* {if(this.state.date !==)
                (
                    <p>{this.state.data.data.address}</p>
                )} */}
                {/* {this.state.data.data.phone} */}
                <h2>Name: </h2> <p>Not in the api data</p>
                <h2>Address: </h2><p>{this.state.address}</p>
                <h2>Phone Number: </h2><p>{this.state.phone}</p>
                <h2>Username: </h2><p>{this.state.username}</p>
                <h2>Password: </h2><p>{this.state.password}</p>
            </div>

        );
    }
}
