import React, { Component } from "react";
import MenuItem from "./MenuItem.js";

export default class Menu extends Component {
    constructor(props) {
        super(props);
        this.state = {
          id:'',
          menu:[],
          food:[]
        };

      }
      componentDidMount(){
        this.loadInitialState()
      }

      async loadInitialState(){

        fetch('https://food-badger.web.app/api/v1/menu', {
        method: 'GET',
        headers: { 'Accept': '*/*',
                    'Access-Control-Allow-Origin': '*'}
        })
        .then(res => res.json())
        .then(data => {
            this.setState({menu: data});


      })

      }

      async addtoCart(d){
        const temp_var = this.state.food;
        temp_var.push(d)
        console.log(temp_var)

        this.props.handleChangeFood(d);


      }




      getMenuItems(){
        let menuitems = [];
        for(const x in this.state.menu){
            menuitems.push (
              <MenuItem key ={x} data={this.state.menu[x]} addtocart={(d)=>this.state.addtoCart(d)}/>
            )
          }
        return menuitems;
      }




    render() {
        return (
            <div>
                <div><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br></div>
                {this.getMenuItems()}

            </div>
        );
    }
}
