import React, { Component } from "react";
import './style.css';


export default class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data:this.props.data.data,
            id: this.props.data.id,
            quantity: 0,
            now: new Date()
        };
        // this.handleChangeUsername = this.handleChangeUsername.bind(this);
        this.handleChangeQuantity = this.handleChangeQuantity.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }
      componentDidMount(){
          console.log(this.props.data)
      }

      handleChangeQuantity(event) {
        console.log(event.target.value);
        this.setState({quantity: event.target.value});

      }

      handleSubmit(event) {
        const d = {
          "quantity": this.state.quantity,
          "price": this.props.data.price,
          "time": this.state.now.getTime(),
         "foodId": this.state.id,

      }
      this.props.addtocart(d)


      }




    render() {
        return (
          <div id="maindiv">
            <div id="divone">
                <img id="image" src={this.state.data.image} alt="Trulli" width="500" height="333"/>
            </div>

            <div id="divtwo">
              <p id="menuname">{this.state.data.name}</p>
              <p id="price">{this.state.data.price}</p>
              <div id="buttons">
              <form >
              <button class="add-button" type="submit" onClick={this.handleSubmit}>Add to Cart!</button>
                        <div id="quantity">
                            <input id="demoInput" type="number" step="1" min="1" max="" name="quantity"  title="Qty" class="inputtext" size="6" pattern="" inputmode="" onChange={this.handleChangeQuantity}/>
                        </div>
              </form>

              </div>
            </div>




                {/* <img src={this.state.data.image} alt={this.state.data.name} width ='180px' height='100px' />
                <h4>Name:</h4> <p>{this.state.data.name}</p>
                <h4>Price:</h4> <p>{this.state.data.price}</p>
                <h4>Availability:</h4> <p>{this.state.data.available}</p> */}

                <hr></hr>


        </div>
        );
    }
}
