import logo from './logo.svg';
import React from 'react';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { useState } from 'react'

import Login from "./login.component";
import SignUp from "./signup.component";

function App() {
  const [state, setState] = useState('start')
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')

  const handleLogIn = (username,password,state) => {
    alert('A name was submitted: ' + username+' , '+ password);
  }


  return (
    <div className="App">
      {state === 'start' && (<>
        <nav className="navbar navbar-expand-lg navbar-light fixed-top">
          <div className="container">
            <Link className="navbar-brand" to={"/sign-in"}>Badger Bytes</Link>
            <div className="collapse navbar-collapse" id="navbarTogglerDemo02">
              <ul className="navbar-nav ml-auto">
                <li className="nav-item">
                  <Link className="nav-link" to={"/sign-in"}>Login</Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link" to={"/sign-up"}>Sign up</Link>
                </li>
              </ul>
            </div>
          </div>
        </nav>

        <div className="auth-wrapper">
          <div className="auth-inner">
            <Switch>
              <Route exact path='/' render={()=> <Login onSubmit={(username_login,password_login,state_login) => handleLogIn(username_login,password_login,state_login)}/>} />
              <Route path="/sign-in" component={Login} />
              <Route path="/sign-up" component={SignUp} />
            </Switch>
          </div>
        </div>
        </>
        )}

      {state === 'add-trip' && (
        <div className="App">
          <h3>Build Sign Up & Login UI Template in React</h3>
        </div>
      )}
    </div>
  );
}

export default App;
