    //   }


    render() {
        return (

            <div>
                <h3> Profile Page</h3>
                {/* {if(this.state.date !==)
                (
                    <p>{this.state.data.data.address}</p>
                )} */}
                {/* {this.state.data.data.phone} */}
                <h2>Name: </h2> <p>Not in the api data</p>
                <h2>Address: </h2><p>{this.state.data.data.address}</p>
                <h2>Phone Number: </h2><p>{this.state.data[0].data.phone}</p>
                <h2>Username: </h2><p>{this.state.data.data[0].username}</p>
                <h2>Password: </h2><p>{this.state.data.data[0].password}</p>
            </div>

        );
    }
}
