import logo from './logo.svg';
import React from 'react';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { useState } from 'react'

import Login from "./login.component";
import SignUp from "./signup.component";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      password: '',
      state_var: 'start',
    };
  }

  handleLogIn(){
    alert('A name was submitted: ' );
  }
  // const [state, setState] = useState('start')
  // const [username, setUsername] = useState('')
  // const [password, setPassword] = useState('')

  // const handleLogIn = (username,password,state) => {
  //   alert('A name was submitted: ' + username+' , '+ password);
  // }

render(){
  return (
    <div className="App">
      {this.state.state_var === 'start' && (<>
        <nav className="navbar navbar-expand-lg navbar-light fixed-top">
          <div className="container">
            <Link className="navbar-brand" to={"/sign-in"}>Badger Bytes</Link>
            <div className="collapse navbar-collapse" id="navbarTogglerDemo02">
              <ul className="navbar-nav ml-auto">
                <li className="nav-item">
                  <Link className="nav-link" to={"/sign-in"}>Login</Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link" to={"/sign-up"}>Sign up</Link>
                </li>
              </ul>
            </div>
          </div>
        </nav>

        <div className="auth-wrapper">
          <div className="auth-inner">
            <Switch>
              <Route exact path='/' render={()=> <Login xyz={() => this.handleLogIn()}/>} />
              <Route path="/sign-in" component={Login} />
              <Route path="/sign-up" component={SignUp} />
            </Switch>
          </div>
        </div>
        </>
        )}

      {this.state.state_var === 'add-trip' && (
        <div className="App">
          <h3>Build Sign Up & Login UI Template in React</h3>
        </div>
      )}
    </div>
  );
  }
}

export default App;
