import React, { Component } from "react";

export default class Profile extends Component {
    constructor(props) {
        super(props);
        this.state = {
          id:this.props.user_id,
          data:{}
        };
        this.handleChangeUsername = this.handleChangeUsername.bind(this);
        this.handleChangePassword = this.handleChangePassword.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }
      componentDidMount(){
          this.loadInitialState();
      }
      async loadInitialState(){

        fetch('https://food-badger.web.app/api/v1/users/'+this.state.id, {
        method: 'GET',
        headers: { 'Accept': '*/*',
                    'Access-Control-Allow-Origin': '*'}
        })
        .then(res => res.json())
        .then(data => {
            console.log(data)
            this.setState({data: data});


      })

      }

      handleChangeUsername(event) {
        this.setState({username: event.target.value});
      }
      handleChangePassword(event) {
        this.setState({password: event.target.value});
      }

      handleSubmit(event) {

        this.props.xyz(this.state.username,this.state.password, "add-trip")
        // alert('A name was submitted: ' + this.state.username+' , '+ this.state.password);
        // event.preventDefault();
      }


    render() {
        return (
            <form>
                <h3>Sign In</h3>

                <div className="form-group">
                    <label>Username</label>
                    <input type="email" className="form-control" placeholder="Enter username" value={this.state.username} onChange={this.handleChangeUsername} />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" className="form-control" placeholder="Enter password" value={this.state.password} onChange={this.handleChangePassword} />
                </div>

                {/* <div className="form-group">
                    <div className="custom-control custom-checkbox">
                        <input type="checkbox" className="custom-control-input" id="customCheck1" />
                        <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                    </div>
                </div> */}

                <button type="submit" className="btn btn-primary btn-block" onClick={this.handleSubmit} >Submit</button>
                {/* <p className="forgot-password text-right">
                    Forgot <a href="#">password?</a>
                </p> */}
            </form>
        );
    }
}
