import React, { Component } from "react";

export default class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data:this.props.data.get("data"),
            id: this.props.data.get("id")
        };
        this.handleChangeUsername = this.handleChangeUsername.bind(this);
        this.handleChangePassword = this.handleChangePassword.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }
      handleChangeUsername(event) {
        this.setState({username: event.target.value});
      }
      handleChangePassword(event) {
        this.setState({password: event.target.value});
      }

      handleSubmit(event) {

        this.props.xyz(this.state.username,this.state.password, "add-trip")
        // alert('A name was submitted: ' + this.state.username+' , '+ this.state.password);
        // event.preventDefault();
      }


    render() {
        return (
            <Card>
                <img src={this.state.data.get("image")} alt={this.state.data.get("name")} />
                <h4>Name:</h4> <p>{this.state.data.get("name")}</p>
                <h4>Price:</h4> <p>{this.state.data.get("price")}</p>
                <h4>Availability:</h4> <p>{this.state.data.get("available")}</p>


            </Card>
        );
    }
}
